import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';


//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Dentist = () => {
  const [dentist, setDentist] = useState(null);
  const { id } = useParams(); // Obtener el parámetro dinámico id de la URL

  useEffect(() => {
    const fetchDentist = async () => {
      try {
        // Aquí deberías realizar la llamada real a la API para obtener los datos del dentista específico
        const response = await fetch(`URL_DE_TU_API/dentists/${id}`);
        const data = await response.json();
        setDentist(data);
      } catch (error) {
        console.error('Error fetching dentist data:', error);
      }
    };
    fetchDentist();
  }, [id]); // Añadir id como dependencia para que se actualice cuando cambie

  if (!dentist) {
    return <div>Cargando...</div>;
  }

  const { name, email, phone, website } = dentist;

  return (
    <>
    <div>
      <h1>Detail Dentist id </h1>
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
      <p>Email: {email}</p>
      <p>Teléfono: {phone}</p>
      <p>Website: {website}</p>
    </div>
    </>
  )
}

export default Dentist
